concept.cypher:
RETURN
  {
    scalar: "Scalar",
    array: [
      "Array Item"
    ],
    object: {
        scalar: "Embedded Object"
    }
  } as object

UNWIND
  [
    {
        key: "key1",
        value: "value1"
    },
    {
        key: "key2",
        value: "value2"
    }
  ] as row
RETURN
  row.key as key, row.value as value
